-    final PointerCoords p = new PointerCoords();
-    p.size = 1;
-    p.pressure = 1;
-    p.x = x;
-    p.y = y;
-
-    return p;
-  }
-
-  @Override
-  public AndroidCommandResult execute(final AndroidCommand command)
-      throws JSONException {
-    try {
-      final PointerCoords[][] pcs = parsePointerCoords(command);
