-  var shotFolder = path.resolve(this.args.tmpDir,
-                                "appium-instruments/Run 1/");
-  if (!fs.existsSync(shotFolder)) {
-    mkdirp.sync(shotFolder);
-  }
-
-  var shotPath = path.resolve(shotFolder, 'screenshot' + guid + '.png');
-  // Retrying the whole screenshot process for three times.
-  async.retry(3,
-    function (cb) {
-      async.waterfall([
-        function (cb) { this.getOrientation(function () { cb(); }); }.bind(this),
-        function (cb) { this.proxy(command, cb); }.bind(this),
-        function (response, cb) {
-          var data;
-          var screenshotWaitTimeout = (this.args.screenshotWaitTimeout || 10) * 1000;
-          logger.debug('Waiting ' + screenshotWaitTimeout + ' ms for screenshot to ge generated.');
-          var startMs = Date.now();
